package br.com.aj2.enumTypes;

import static com.google.common.collect.Lists.newArrayList;

import java.util.List;

import javax.inject.Named;
import javax.inject.Singleton;
import javax.faces.model.SelectItem;

import br.com.aj2.enumTypes.TipoPessoa;

/**
 * Helper to support the h:selectOneRadio and h:selectOneMenu for TipoPessoa.
 */
@Named
@Singleton
public class TipoPessoaItems {

    /**
     * Returns a list of SelectItem/TipoPessoa ready to use in a h:selectOneRadio, h:selectOneMenu or p:selectManyCheckbox tags
     */
    public List<SelectItem> getList() {
        List<SelectItem> result = newArrayList();
        for (TipoPessoa tipoPessoa : TipoPessoa.values()) {
            result.add(new SelectItem(tipoPessoa, tipoPessoa.getLabel()));
        }

        return result;
    }
}
