package br.com.aj2.printer;

import java.util.Locale;
import javax.inject.Named;
import javax.inject.Singleton;
import br.com.aj2.domain.Equipamentos;
import br.com.aj2.printer.DiscoverablePrinter;

/**
 * {@link org.springframework.format.Printer} for {@link Equipamentos} 
 *
 * @see org.springframework.format.Printer
 * @see DiscoverablePrinter
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class EquipamentosPrinter extends DiscoverablePrinter<Equipamentos> {
    public EquipamentosPrinter() {
        super(Equipamentos.class);
    }

    @Override
    public String print(Equipamentos equipamentos, Locale locale) {
        if (equipamentos == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, equipamentos.getDescricao());
        return ret.toString();
    }
}